<?php // ͡° ͜ ʖ ͡ ͡°function selectores($tabla, $tabla2, $conn, $idactual){$s = " ";//$s .= "Busqueda <input type=\"text\" id=\"realtxt\" onkeyup=\"searchSel()\">";$s .= "<SELECT id=\"".$tabla2."\" name=\"".$tabla2."\">";$s .= "<OPTION value=\"0\">Seleccione...";$sql = "select * from " . $tabla;$query = mysql_query($sql, $conn);$id = "id". $tabla2;$nombre = $tabla2;while($array = mysql_fetch_assoc($query)){	//$s .= "<OPTION value=\"". $array[$nombre] ."\">" . $array[$nombre];		if($idactual == null)	{	$s .= "<OPTION value=\"". $array[$nombre] ."\">" . $array[$nombre];	$idactual++;	}	else	{			if($array[$id] == $idactual)		{		@$s .= "<OPTION value=\"". $array[$nombre] ."\" selected=\"selected\">" . $array[$nombre];		}		else		{		@$s .= "<OPTION value=\"". $array[$nombre] ."\">" . $array[$nombre];		}		$idactual++;	}	}$s .= "</SELECT>";return $s;}?>